import React, { useState } from 'react';
import axios from 'axios';
import AuthLayout from '../components/AuthLayout.tsx';
import SocialButtons from '../components/SocialButtons.tsx';
import FriendsFront from '../assets/FrontFriends.jpg';


// Ð‘Ð°Ð·Ð¾Ð²Ñ‹Ð¹ URL Go-Ð±ÑÐºÐµÐ½Ð´Ð°
const API_BASE_URL = 'http://localhost:8080/api/auth'; 

const LoginPage: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // ðŸ’¡ ÐšÐ›Ð®Ð§Ð•Ð’ÐžÐ™ Ð—ÐÐŸÐ ÐžÐ¡ Ð¡ withCredentials: true
      const response = await axios.post(
        `${API_BASE_URL}/login`,
        {
          email: email,
          password: password,
        },
        {
          // ÐžÐ‘Ð¯Ð—ÐÐ¢Ð•Ð›Ð¬ÐÐž Ð´Ð»Ñ Ð¾Ñ‚Ð¿Ñ€Ð°Ð²ÐºÐ¸ ÐºÑƒÐºÐ¸ Ð½Ð° Go-Ð±ÑÐºÐµÐ½Ð´ Ð¸ Ð¿Ð¾Ð»ÑƒÑ‡ÐµÐ½Ð¸Ñ ÐºÑƒÐºÐ¸ Ð² Ð¾Ñ‚Ð²ÐµÑ‚
          withCredentials: true, 
        }
      );

      // Ð’ ÑÐ»ÑƒÑ‡Ð°Ðµ ÑƒÑÐ¿ÐµÑ…Ð°:
      console.log('Login successful:', response.data);
      // Ð—Ð´ÐµÑÑŒ Ð¼Ð¾Ð¶Ð½Ð¾ ÑÐ¾Ñ…Ñ€Ð°Ð½Ð¸Ñ‚ÑŒ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ Ð² Ð³Ð»Ð¾Ð±Ð°Ð»ÑŒÐ½Ð¾Ðµ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ (Context/Redux)
      // Ð¸ Ð¿ÐµÑ€ÐµÐ½Ð°Ð¿Ñ€Ð°Ð²Ð¸Ñ‚ÑŒ Ð½Ð° /dashboard.
      alert(`Ð’Ñ…Ð¾Ð´ ÑƒÑÐ¿ÐµÑˆÐµÐ½! Ð”Ð¾Ð±Ñ€Ð¾ Ð¿Ð¾Ð¶Ð°Ð»Ð¾Ð²Ð°Ñ‚ÑŒ, ${response.data.user.username}`);

    } catch (err) {
      if (axios.isAxiosError(err) && err.response) {
        // ÐžÑ‚Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ðµ Ð¾ÑˆÐ¸Ð±ÐºÐ¸ Ñ Ð±ÑÐºÐµÐ½Ð´Ð° (Ð½Ð°Ð¿Ñ€Ð¸Ð¼ÐµÑ€, "Invalid credentials" Ð¸Ð»Ð¸ "Your account has been blocked")
        setError(err.response.data.error || 'Login failed');
      } else {
        setError('An unexpected error occurred. Check server connection.');
      }
    } finally {
      setLoading(false);
    }
  };

  const LoginIllustration = () => (
    <div style={{ textAlign: 'center' }}>
      <img src={FriendsFront} alt="Login friends" style={{ maxWidth: '80%', height: 'auto' }} />
    </div>
  );

  return (
    <AuthLayout illustration={<LoginIllustration />}>
      <h1 className="auth-title">Welcome to sneat! ðŸ‘‹</h1>
      <p className="auth-subtitle">Please sign-in to your account and start the adventure</p>

      <form onSubmit={handleSubmit}>
        {/* ÐŸÐ¾Ð»Ðµ Email */}
        <div className="form-field-group">
          <label htmlFor="email" className="form-label">Email or Username</label>
          <input 
            type="text" 
            id="email" 
            className="form-input" 
            placeholder="Enter your email or username" 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        {/* ÐŸÐ¾Ð»Ðµ Password */}
        <div className="form-field-group">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '6px' }}>
            <label htmlFor="password" className="form-label" style={{ marginBottom: 0 }}>Password</label>
            <a href="/forgot-password" style={{ fontSize: '0.875rem', color: '#696cff', textDecoration: 'none' }}>Forgot password?</a>
          </div>
          <input 
            type="password" 
            id="password" 
            className="form-input" 
            placeholder="Â·Â·Â·Â·Â·Â·Â·Â·Â·Â·Â·Â·"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        {/* Checkbox "Remember me" */}
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <label style={{ fontSize: '0.875rem', color: '#3f4254', display: 'flex', alignItems: 'center' }}>
            <input type="checkbox" style={{ marginRight: '8px' }} />
            Remember me
          </label>
        </div>
        
        {/* ÐžÑ‚Ð¾Ð±Ñ€Ð°Ð¶ÐµÐ½Ð¸Ðµ Ð¾ÑˆÐ¸Ð±ÐºÐ¸ */}
        {error && <p style={{ color: 'red', textAlign: 'center', fontSize: '0.875rem', marginBottom: '10px' }}>{error}</p>}


        <button type="submit" className="primary-button" disabled={loading}>
          {loading ? 'Logging In...' : 'Login'}
        </button>
      </form>

      <p style={{ textAlign: 'center', fontSize: '0.9rem', marginTop: '20px' }}>
        New on our platform? <a href="/register" style={{ color: '#696cff', textDecoration: 'none', fontWeight: 500 }}>Create an account</a>
      </p>

      <div className="separator">or</div>
      <SocialButtons />
    </AuthLayout>
  );
};

export default LoginPage;
