import React, { useState } from 'react';
import AuthLayout from '../components/AuthLayout.tsx';
import SocialButtons from '../components/SocialButtons.tsx';

const LoginPage: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    console.log('Login attempt:', { email, password });
    // –ó–¥–µ—Å—å –±—É–¥–µ—Ç –≤—ã–∑–æ–≤ Go-–±—ç–∫–µ–Ω–¥–∞
  };

  const LoginIllustration = () => (
    // –ó–∞–≥–ª—É—à–∫–∞, –∏–º–∏—Ç–∏—Ä—É—é—â–∞—è –∏–ª–ª—é—Å—Ç—Ä–∞—Ü–∏—é —Å —Ä–∞–∫–µ—Ç–æ–π
    <div style={{ textAlign: 'center' }}>
      <img src="https://i.imgur.com/8y9pXpC.png" alt="Login Illustration" style={{ maxWidth: '80%', height: 'auto' }} />
      {/* –ò–ª–ª—é—Å—Ç—Ä–∞—Ü–∏—è –≤–∑—è—Ç–∞ –¥–ª—è –ø—Ä–∏–º–µ—Ä–∞ –∏–∑ –ø—É–±–ª–∏—á–Ω–æ–≥–æ –∏—Å—Ç–æ—á–Ω–∏–∫–∞ */}
    </div>
  );

  return (
    <AuthLayout illustration={<LoginIllustration />}>
      <h1 className="auth-title">Welcome to sneat! üëã</h1>
      <p className="auth-subtitle">Please sign-in to your account and start the adventure</p>

      <form onSubmit={handleSubmit}>
        {/* –ü–æ–ª–µ Email */}
        <div className="form-field-group">
          <label htmlFor="email" className="form-label">Email or Username</label>
          <input 
            type="text" 
            id="email" 
            className="form-input" 
            placeholder="Enter your email or username" 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        {/* –ü–æ–ª–µ Password */}
        <div className="form-field-group">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '6px' }}>
            <label htmlFor="password" className="form-label" style={{ marginBottom: 0 }}>Password</label>
            <a href="/forgot-password" style={{ fontSize: '0.875rem', color: '#696cff', textDecoration: 'none' }}>Forgot password?</a>
          </div>
          <input 
            type="password" 
            id="password" 
            className="form-input" 
            placeholder="¬∑¬∑¬∑¬∑¬∑¬∑¬∑¬∑¬∑¬∑¬∑¬∑"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        {/* Checkbox "Remember me" */}
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '24px' }}>
          <label style={{ fontSize: '0.875rem', color: '#3f4254', display: 'flex', alignItems: 'center' }}>
            <input type="checkbox" style={{ marginRight: '8px' }} />
            Remember me
          </label>
        </div>

        <button type="submit" className="primary-button">
          Login
        </button>
      </form>

      <p style={{ textAlign: 'center', fontSize: '0.9rem', marginTop: '24px' }}>
        New on our platform? <a href="/register" style={{ color: '#696cff', textDecoration: 'none', fontWeight: 500 }}>Create an account</a>
      </p>

      <div className="separator">or</div>
      <SocialButtons />
    </AuthLayout>
  );
};

export default LoginPage;
