// src/pages/LoginPage.tsx

import React, { useState } from 'react';
import axios from 'axios'; // üí° –ò–º–ø–æ—Ä—Ç–∏—Ä—É–µ–º Axios
import AuthLayout from '../components/AuthLayout';
import SocialButtons from '../components/SocialButtons';

// !!! –ë–∞–∑–æ–≤—ã–π URL –≤–∞—à–µ–≥–æ Go-–±—ç–∫–µ–Ω–¥–∞
const API_BASE_URL = 'http://localhost:8080/api/auth'; 

const LoginPage: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      // üí° –ö–õ–Æ–ß–ï–í–û–ô –ó–ê–ü–†–û–° –° withCredentials: true
      const response = await axios.post(
        `${API_BASE_URL}/login`,
        {
          email: email,
          password: password,
        },
        {
          // –≠–¢–û –û–ë–Ø–ó–ê–¢–ï–õ–¨–ù–û –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏ –∫—É–∫–∏ –Ω–∞ Go-–±—ç–∫–µ–Ω–¥ –∏ –ø–æ–ª—É—á–µ–Ω–∏—è –∫—É–∫–∏ –≤ –æ—Ç–≤–µ—Ç
          withCredentials: true, 
        }
      );

      // –í —Å–ª—É—á–∞–µ —É—Å–ø–µ—Ö–∞:
      console.log('Login successful:', response.data);
      // –ü–æ—Å–ª–µ —É—Å–ø–µ—à–Ω–æ–≥–æ –≤—Ö–æ–¥–∞ –º–æ–∂–Ω–æ –ø–µ—Ä–µ–Ω–∞–ø—Ä–∞–≤–∏—Ç—å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è, 
      // –Ω–∞–ø—Ä–∏–º–µ—Ä, –Ω–∞ –≥–ª–∞–≤–Ω—É—é —Å—Ç—Ä–∞–Ω–∏—Ü—É: navigate('/dashboard');
      alert('–í—Ö–æ–¥ —É—Å–ø–µ—à–µ–Ω! –¢–æ–∫–µ–Ω —Å–æ—Ö—Ä–∞–Ω–µ–Ω –≤ HTTP-Only Cookie.');

    } catch (err) {
      if (axios.isAxiosError(err) && err.response) {
        // –û—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ –æ—à–∏–±–∫–∏ —Å –±—ç–∫–µ–Ω–¥–∞ (–Ω–∞–ø—Ä–∏–º–µ—Ä, "Invalid credentials")
        setError(err.response.data.error || 'Login failed');
      } else {
        setError('An unexpected error occurred.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <AuthLayout /* ... */>
      {/* ... –ó–∞–≥–æ–ª–æ–≤–∫–∏ ... */}

      <form onSubmit={handleSubmit}>
        {/* ... –ü–æ–ª—è email –∏ password ... */}
        
        {/* –û—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ –æ—à–∏–±–∫–∏ */}
        {error && <p style={{ color: 'red', textAlign: 'center', margin: '10px 0' }}>{error}</p>}

        <button type="submit" className="primary-button" disabled={loading}>
          {loading ? 'Logging In...' : 'Login'}
        </button>
      </form>
      
      {/* ... –û—Å—Ç–∞–ª—å–Ω–æ–π –∫–æ–Ω—Ç–µ–Ω—Ç ... */}
    </AuthLayout>
  );
};

export default LoginPage;
