// src/pages/RegisterPage.tsx

import React, { useState } from 'react';
import axios from 'axios'; // üí° –ò–º–ø–æ—Ä—Ç–∏—Ä—É–µ–º Axios
import AuthLayout from '../components/AuthLayout';
import SocialButtons from '../components/SocialButtons';

// !!! –ë–∞–∑–æ–≤—ã–π URL –≤–∞—à–µ–≥–æ Go-–±—ç–∫–µ–Ω–¥–∞
const API_BASE_URL = 'http://localhost:8080/api/auth'; 

const RegisterPage: React.FC = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [agreed, setAgreed] = useState(false);
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setMessage('');
    setLoading(true);

    try {
      const response = await axios.post(
        `${API_BASE_URL}/register`,
        {
          username,
          email,
          password,
        }
        // –ó–∞–ø—Ä–æ—Å—É —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏ withCredentials: true –Ω–µ —Ç—Ä–µ–±—É–µ—Ç—Å—è, 
        // —Ç–∞–∫ –∫–∞–∫ –∫—É–∫–∏ –µ—â–µ –Ω–µ —É—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞—é—Ç—Å—è.
      );

      // –í —Å–ª—É—á–∞–µ —É—Å–ø–µ—Ö–∞:
      console.log('Registration successful:', response.data);
      setMessage(response.data.message || 'Registration successful! Now you can log in.');
      // –û—á–∏—Å—Ç–∫–∞ —Ñ–æ—Ä–º—ã
      setUsername('');
      setEmail('');
      setPassword('');
      setAgreed(false);

    } catch (err) {
      if (axios.isAxiosError(err) && err.response) {
        // –û—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ –æ—à–∏–±–∫–∏ —Å –±—ç–∫–µ–Ω–¥–∞ (–Ω–∞–ø—Ä–∏–º–µ—Ä, "User already exists")
        setError(err.response.data.error || 'Registration failed');
      } else {
        setError('An unexpected error occurred.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <AuthLayout /* ... */>
      {/* ... –ó–∞–≥–æ–ª–æ–≤–∫–∏ ... */}

      <form onSubmit={handleSubmit}>
        {/* ... –ü–æ–ª—è username, email, password ... */}
        {/* ... –ß–µ–∫–±–æ–∫—Å "I agree" ... */}

        {/* –û—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ –æ—à–∏–±–∫–∏/—É—Å–ø–µ—Ö–∞ */}
        {error && <p style={{ color: 'red', textAlign: 'center', margin: '10px 0' }}>{error}</p>}
        {message && <p style={{ color: 'green', textAlign: 'center', margin: '10px 0' }}>{message}</p>}

        <button type="submit" className="primary-button" disabled={loading || !agreed}>
          {loading ? 'Signing Up...' : 'Sign Up'}
        </button>
      </form>
      
      {/* ... –û—Å—Ç–∞–ª—å–Ω–æ–π –∫–æ–Ω—Ç–µ–Ω—Ç ... */}
    </AuthLayout>
  );
};

export default RegisterPage;
